"use strict";
function expectBbox(element, expectedBbox) {
    var bbox = element.getBoundingClientRect();
    expect(Math.round(bbox.left)).to.equal(expectedBbox.left);
    expect(Math.round(bbox.top)).to.equal(expectedBbox.top);
    expect(Math.round(bbox.width)).to.equal(expectedBbox.width);
    expect(Math.round(bbox.height)).to.equal(expectedBbox.height);
}
describe("Relative projection targets: Delay", function () {
    it("Child correctly follows parent", function () {
        cy.visit("?test=layout-relative-delay")
            .wait(50)
            .get("#parent")
            .should(function (_a) {
            var $parent = _a[0];
            expectBbox($parent, {
                top: 0,
                left: 0,
                width: 200,
                height: 200,
            });
        })
            .get("#child")
            .should(function (_a) {
            var $child = _a[0];
            expectBbox($child, {
                top: 0,
                left: 0,
                width: 100,
                height: 100,
            });
        })
            .get("#parent")
            .trigger("click")
            .wait(50)
            .get("#parent")
            .should(function (_a) {
            var $parent = _a[0];
            expectBbox($parent, {
                top: 100,
                left: 100,
                width: 300,
                height: 200,
            });
        })
            .get("#child")
            .should(function (_a) {
            var $child = _a[0];
            expectBbox($child, {
                top: 100,
                left: 100,
                width: 100,
                height: 100,
            });
        });
    });
});
