"use strict";
function expectBbox(element, expectedBbox) {
    var bbox = element.getBoundingClientRect();
    expect(Math.round(bbox.left)).to.equal(expectedBbox.left);
    expect(Math.round(bbox.top)).to.equal(expectedBbox.top);
    expect(Math.round(bbox.width)).to.equal(expectedBbox.width);
    expect(Math.round(bbox.height)).to.equal(expectedBbox.height);
}
describe("Resize window", function () {
    it("Finishes the animation and blocks animation on immediate layout animations until 250ms", function () {
        cy.visit("?test=layout-resize")
            .wait(50)
            .get("#box")
            .should(function (_a) {
            var $box = _a[0];
            expectBbox($box, {
                top: 0,
                left: 0,
                width: 100,
                height: 100,
            });
        })
            .trigger("click")
            .wait(50)
            .viewport(200, 200)
            .wait(100)
            .get("#box")
            .should(function (_a) {
            var $box = _a[0];
            expectBbox($box, {
                top: 100,
                left: 100,
                width: 200,
                height: 200,
            });
        })
            .trigger("click", { force: true })
            .wait(50)
            .should(function (_a) {
            var $box = _a[0];
            expectBbox($box, {
                top: 0,
                left: 0,
                width: 100,
                height: 100,
            });
        })
            .wait(200)
            .trigger("click", { force: true })
            .wait(300)
            .should(function (_a) {
            var $box = _a[0];
            var bbox = $box.getBoundingClientRect();
            expect(Math.round(bbox.top)).not.to.equal(0);
            expect(Math.round(bbox.top)).not.to.equal(100);
        });
    });
});
